mapper.insert:
insert into users (id, username, password, 
  telephone, email, right, 
  state, lastLoginTime, registerTime
  )
values (#{id,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{telephone,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, #{right,jdbcType=INTEGER}, 
  #{state,jdbcType=INTEGER}, #{lastlogintime,jdbcType=TIMESTAMP}, #{registertime,jdbcType=TIMESTAMP}
  )

mapper.select:
select id, username, password, telephone, email, right, state, lastLoginTime, registerTime
from users
where id = #{id,jdbcType=INTEGER}
